import React, { Component } from 'react';

import Card from 'react-bootstrap/Card'

class PlayerCard extends Component {
    render() {
        return (
          <Card style={{ width: '18rem'}}>
            <Card.Body>

              <Card.Title>{this.props.info.name}</Card.Title>

              <Card.Subtitle style={{ paddingBottom: 5 }}>Player Type</Card.Subtitle>
              {this.props.info.player_type.map(trait => (
                  <Card.Subtitle key={trait} style={{ fontSize: 12, paddingBottom: 4 }}>{trait}</Card.Subtitle>
              ))}

              <Card.Subtitle style={{ paddingBottom: 5, paddingTop: 2 }}>Cost</Card.Subtitle>
              {this.props.info.cost.map(trait => (
                  <Card.Subtitle key={trait} style={{ fontSize: 12, paddingBottom: 4 }}>{trait}</Card.Subtitle>
              ))}

              <Card.Subtitle style={{ paddingBottom: 5, paddingTop: 2 }}>Movement Allowance</Card.Subtitle>
              {this.props.info.movement_allowance.map(trait => (
                  <Card.Subtitle key={trait} style={{ fontSize: 12, paddingBottom: 4 }}>{trait}</Card.Subtitle>
              ))}

              <Card.Subtitle style={{ paddingBottom: 5, paddingTop: 2 }}>Strength</Card.Subtitle>
              {this.props.info.strength.map(trait => (
                  <Card.Subtitle key={trait} style={{ fontSize: 12, paddingBottom: 4 }}>{trait}</Card.Subtitle>
              ))}

              <Card.Subtitle style={{ paddingBottom: 5, paddingTop: 2 }}>Agility</Card.Subtitle>
              {this.props.info.agility.map(trait => (
                  <Card.Subtitle key={trait} style={{ fontSize: 12, paddingBottom: 4 }}>{trait}</Card.Subtitle>
              ))}

              <Card.Subtitle style={{ paddingBottom: 5, paddingTop: 2 }}>Armour Value</Card.Subtitle>
              {this.props.info.armour_value.map(trait => (
                  <Card.Subtitle key={trait} style={{ fontSize: 12, paddingBottom: 4 }}>{trait}</Card.Subtitle>
              ))}

              <Card.Subtitle style={{ paddingBottom: 5, paddingTop: 2 }}>Skills</Card.Subtitle>
              {this.props.info.skills.map(trait => (
                  <Card.Subtitle key={trait} style={{ fontSize: 12, paddingBottom: 4 }}>{trait}</Card.Subtitle>
              ))}

            </Card.Body>
        </Card>
        )
    }
}


export default PlayerCard
