import React from 'react';
import { connect } from 'react-redux'

const PlayerDisplay = props => {
    const info = props.info

    const displayObject = () => {
        if (info) {
            return (
                <div>
                    <h1>{info.name}</h1>
                    <h1>{info.player_type}</h1>
                    <h1>{info.cost}</h1>
                    <h1>{info.movement_allowance}</h1>
                    <h1>{info.strength}</h1>
                    <h1>{info.agility}</h1>
                    <h1>{info.armour_value}</h1>
                    <h1>{info.skills}</h1>            
                </div>
            )
        }

        else {
            return <div>Loading</div>
        }
    };

    return (
        <div>
            {displayObject()}
        </div>
    )
};


const mapStateToProps = state => {
    return {
        info: state.players.object
    }
};

export default connect(mapStateToProps)(PlayerDisplay);
