import React, { Component } from 'react';
import { connect } from 'react-redux';
import { sortTeams } from ".././actions/dataActions";

import Row from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";

class SortButton extends Component {
  constructor() {
      super();
      this.state = { teams: [] };
    }

    handleTeamSort = e => {
        this.props.sortTeams()
    };

    render() {
      return (
        <Container>
            <Row>
              <div>
                  <button id="sort-button" title="Sort Teams" onClick={this.handleTeamSort}>Sort Teams</button>
              </div>
            </Row>
        </Container>
        )
      }

    }

    const mapStateToProps = state => {
        return {
          teams: state.teams
        }
    };

    const mapDispatchToProps = dispatch => {
        return {
            sortTeams: params => dispatch(sortTeams(params))
        }
    };


export default connect(mapStateToProps, mapDispatchToProps)(SortButton)
