import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";

import TeamCard from "../components/TeamCard";
import PlayerCard from "../components/PlayerCard";
import StarPlayerCard from "../components/StarPlayerCard";
import { displayObject } from "../actions/dataActions";

import CardColumns from "react-bootstrap/CardColumns";

class DataContainer extends Component {
  state = {
    altSort: false,
    // Copy prop into state so we can render from state instead of props
    teams: this.props.teamsDefaultOrder
  };

  /**
   * Sorts the teams into default sorted order.
   */
  sortTeamsDefaultOrder = () => {
    // if alt sorted, allow sorting back to default order
    !this.state.altSort &&
      this.setState({
        teams: this.props.teamsDefaultOrder
      });
  };

  /**
   * Sorts the teams into some alternative sorted order.
   */
  sortTeamsAltOrder = () => {
    this.props.teamsDefaultOrder.cards.sort(function(team1, team2) {
        if (team1.name < team2.name) {
          return -1;
        }
        if (team1.name > team2.name) {
          return 1;
        }
        return 0;
      });

    // if default sorted, allow sorting to alt order
    !this.state.altSort &&
      this.setState({
        teams: this.props.teamsDefaultOrder
      });
  };

  displayCards = () => {
    switch (this.props.path) {
      case "teams":
        // Now render `this.state.teams`
        return this.state.teams.cards.map(card => (
          <NavLink
            style={{ color: "black" }}
            to={`/teams/${card.id}`}
            key={card.id}
          >
            <TeamCard view={this.props.displayObject} info={card} />
          </NavLink>
        ));

      case "players":
        return this.props.players.cards.map(card => (
          <NavLink
            style={{ color: "black" }}
            to={`/players/${card.id}`}
            key={card.id}
          >
            <PlayerCard view={this.props.displayObject} info={card} />
          </NavLink>
        ));

      case "star_players":
        return this.props.star_players.cards.map(card => (
          <NavLink style={{ color: "black" }} to={`/star_players/${card.id}`} key={card.id}>
            <StarPlayerCard view={this.props.displayObject} info={card} />
          </NavLink>
        ));

      default:
        return <div>Empty</div>;
    }
  };

  render() {
    return (
      <Fragment>
        <CardColumns>{this.sortTeamsDefaultOrder}{this.displayCards()}</CardColumns>

        {/* button group to toggle between team sort orders */}
        <div>
          <button type="button" onClick={this.sortTeamsAltOrder}>
            Alt Order
          </button>
          <button type="button" onClick={this.sortTeamsDefaultOrder}>
            Default Order
          </button>
        </div>
      </Fragment>
    );
  }
}

const mapStateToProps = state => ({
  // Default order sort teams from state
  teamsDefaultOrder: state.teams, // need to define `defaultSortOrderFn`!
  players: state.players,
  star_players: state.star_players
});

const mapDispatchToProps = {
  displayObject
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(DataContainer);
