import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchTeams } from ".././actions/dataActions";
import TeamCard from '../components/TeamCard'

import Row from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";

class SortButton extends Component {
  state = {
    teams: [],
  }

  componentDidMount() {
    this.setState({ teams })
  }

  sortTeams = () => {
    const { teams } = this.state;
    teams.sort(function(team1, team2) {
      if (team1.name < team2.name) {
        return -1;
      }
      if (team1.name > team2.name) {
        return 1;
      }
    })
    this.setState({ teams })
  }

    render() {
      const { teams } = this.state;

      return (
        <Container>
            <Row>
            {teams.map(team => (
              <div>
                  <button id="team-sort-button" title="Sort Teams" onClick={this.onSort}>Sort Teams</button>
              </div>
              ))}
            </Row>
        </Container>
       )
      }


    }

    const mapStateToProps = state => {
        return {
        }
    };

    const mapDispatchToProps = dispatch => {
        return {
            fetchTeams: params => dispatch(fetchTeams(params)),
        }
    };

export default connect(mapStateToProps, mapDispatchToProps)(SortButton)
