export default function TeamsReducer(state = {
    cards: [],
    loading: false
}, action) {
    switch (action.type) {
        case 'LOADING_TEAMS':
            return {
                ...state,
            };

        case 'ADD_TEAMS':
            return {
                ...state,
                cards: action.cards,
            };

        case 'DISPLAY_TEAMS':
            return {
                ...state,
                object: action.object,
            };

        default:
            return state
    }
}
