import React, { Component } from 'react';
import { connect } from 'react-redux';
import { sortTeams } from ".././actions/dataActions";

import { NavLink } from 'react-router-dom';

import TeamCard from '../components/TeamCard'
import { displayObject } from '../actions/dataActions'


import Row from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";

class SortButton extends Component {
  constructor() {
      super();
      this.state = { teams: [] };
    }

    handleTeamSort = e => {
        this.props.sortTeams()
    };

    render() {
      return (
        <Container>
            <Row>
              <div>
                  <button id="sort-button" title="Sort Teams" onClick={this.handleTeamSort}>Sort Teams</button>
                    return (this.props.teams.cards.map(card => (
                        <NavLink style={{ color: "black" }} to={`/teams/${card.id}`} key={card.id}><TeamCard view={this.props.displayObject} info={card} /></NavLink>
                    )));
              </div>
            </Row>
        </Container>
        )
      }

    }

    const mapStateToProps = state => {
        return {
          teams: state.teams
        }
    };

    const mapDispatchToProps = dispatch => {
        return {
            sortTeams: params => dispatch(sortTeams(params)),
        }
    };


export default connect(mapStateToProps, mapDispatchToProps)(SortButton)
