import React, {useState} from 'react';

import Card from 'react-bootstrap/Card'

import LikeButton from "../components/LikeButton";

const TeamCard = props => {
  let [myStyles, setStyle] = useState({border: '8px solid black', padding: '1rem'})

    const handleClick = (event) => {
      props.view(props.info.id, "teams", 'DISPLAY_TEAMS');
      console.log('bubbled up event from ' + event.target.id);
      if(event.target.id !== "likeButton"){
        setStyle({border: '8px solid red', padding: '1rem'})
      }
    }

    return (
        <Card onClick={handleClick} style={myStyles}>
            <Card.Body>
                <Card.Title>{props.info.name}<LikeButton /></Card.Title>
            </Card.Body>
            <Card.Img variant="bottom" style={{ height: '12rem' }} src={props.info.image} />
        </Card>
    )
}

export default TeamCard
