import React, { Component } from 'react';
import { connect } from 'react-redux';
import { sortTeams } from ".././actions/dataActions";

import TeamCard from '../components/TeamCard'
import { displayObject } from '../actions/dataActions'

import CardColumns from 'react-bootstrap/CardColumns'

import Row from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";

class SortButton extends Component {
  constructor() {
      super();
      this.state = { teams: [] };
    }

    displayCards = () => {
        switch(this.props.path) {
            case "teams":
                return (this.props.teams.cards.map(card => (
                    <NavLink style={{ color: "black" }} to={`/teams/${card.id}`} key={card.id}><TeamCard view={this.props.displayObject} info={card} /></NavLink>
                )));
                default:
                    return (<div>Empty</div>)
            }
        };

    handleTeamSort = e => {
        this.props.sortTeams()
    };

    render() {
        <Container>
            <Row>
              <div>
                  <button id="sort-button" title="Sort Teams" onClick={this.handleTeamSort}>Sort Teams</button>
              </div>
              <CardColumns>
                  {this.displayCards()}
              </CardColumns>
            </Row>
        </Container>

      }

    }

    const mapStateToProps = state => {
        return {
          teams: state.teams,
        }
    };

    const mapDispatchToProps = dispatch => {
        return {
            sortTeams: params => dispatch(sortTeams(params)),
            displayObject: (id, category, type) => dispatch(displayObject(id, category, type)),
        }
    };


export default connect(mapStateToProps, mapDispatchToProps)(SortButton)
