export default function TeamsReducer(state = {
    cards: [],
    loading: false
}, action) {
  switch (action.type) {
      case 'ADD_TEAMS':
          return {
              ...state,
              cards: action.cards,
          };

      case 'DISPLAY_TEAMS':
          return {
              ...state,
              piece: action.piece,
          };

      default:
          return state
    }
}
