import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchTeams } from ".././actions/dataActions";

import Row from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";

class TeamFilter extends Component {
  constructor() {
      super(); // used to access and call functions on an object's parent
      this.state = { teams: [] };
    }

    handleTeamFetch = e => {
        this.props.fetchTeams()
    };

    sortTeams = e => {
      const { teams } = this.state;
      teams.sort(function(team1, team2) {
        if (team1.name < team2.name) {
          return -1;
        }
        if (team1.name > team2.name) {
          return 1;
        }
      })
      this.setState({ teams })
    }


    render() {
      return (
        <Container>
            <Row>
              <div>
                  <button id="team-show-button" title="Show All Teams" onClick={this.handleTeamFetch}>Show All Teams</button>
              </div>
              <div>
                  <button id="team-sort-button" title="Sort Teams" onClick={this.onSort}>Sort Teams</button>
              </div>
            </Row>
        </Container>
        )
      }


    }

    const mapStateToProps = state => {
        return {
        }
    };

    const mapDispatchToProps = dispatch => {
        return {
            fetchTeams: params => dispatch(fetchTeams(params)),
        }
    };


export default connect(mapStateToProps, mapDispatchToProps)(TeamFilter)
