      this.props.fetchTeams(name);
      teams.sort(function(team1, team2) {
        if (team1.name < team2.name) {
          return -1;
        }
        if (team1.name > team2.name) {
          return 1;
        }
      })
    }

    render() {
      return (
        <Container>
            <Row>
              <div>
                  <button id="team-sort-button" title="Sort Teams" onClick={this.handleSorting}>Sort Teams</button>
