import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchTeams } from ".././actions/dataActions";
// import { sortTeams } from ".././actions/dataActions";

import Row from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";

class TeamFilter extends Component {
  constructor() {
      super(); // used to access and call functions on an object's parent
      this.state = { teams: [] };
      this.handleTeamSort = this.handleTeamSort.bind(this);
    }

    handleTeamFetch = e => {
        this.props.fetchTeams()
    };

    handleTeamSort(e) {
      this.setState({
        teams: [...this.state.teams].sort((team1, team2) =>
          team1.name.localeCompare(team2.name))
      });
    }

