import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchCards, fetchTeams } from ".././actions/dataActions";

import Dropdown from 'react-bootstrap/Dropdown'
import DropdownButton from 'react-bootstrap/DropdownButton'

class TeamFilter extends Component {
  constructor() {
      super();
      this.state = {
         data: [],
         teams: {}
       };
    }

    async fetchButtonTeams() {
      const response = await fetch(`/api/teams`);
      const json = await response.json();
      this.setState({ data: json });
    }

    componentDidMount(){
      this.fetchButtonTeams()
    }

    componentWillMount() {
        fetch('/api/teams/id')
            .then(res => res.json())
            .then(res => {
                this.setState({
                    ...this.state, // Not required but just a heads up on using mutation
                    teams: res
                })
            })
            .catch((error => {
                console.error(error);
            }));
    }

    handleTeamSelection = e => {
        this.props.setTeam(e.target.title);
        this.props.fetchTeams(e.target.title)
    };


    render() {


      return (
          <DropdownButton id="dropdown-team-button" title={this.props.team_name}>
              {this.state.data.map(cls => (
                  <div key={cls}>
                      <p><strong>Id: {this.state.team.IDTEAM}</strong></p>
                      <Dropdown.Item onClick={this.handleTeamSelection} title={cls.name} href={'/teams/'+ cls.id}>{cls.name}</Dropdown.Item>
                  </div>
              ))}
          </DropdownButton>
        )
      }


    }

    const mapStateToProps = state => {
        return {
            team_name: state.team_name
        }
    };

const mapDispatchToProps = dispatch => {
        return {
            fetchCards: path => dispatch(fetchCards(path)),
            fetchTeams: params => dispatch(fetchTeams(params)),
            setTeam: team_name => dispatch({ type: "SET_TEAM", team_name })
        }
    };




export default connect(mapStateToProps, mapDispatchToProps)(TeamFilter)
