import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchTeams } from ".././actions/dataActions";
import TeamCard from '../components/TeamCard'

import Row from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";

class SortButton extends Component {
  constructor() {
      super(); // used to access and call functions on an object's parent
      this.state = { teams: [] };
    }

    handleTeamFetch = e => {
        this.props.fetchTeams()
    };


    render() {
      return (this.props.sort_teams.map(card => (
        <Container>
            <Row>
              <div>
                  <button id="team-sort-button" title="Sort Teams" onClick={this.handleTeamFetch}>Sort Teams</button>
              </div>
            </Row>
        </Container>
        )));
      }


    }

    const mapStateToProps = state => {
        return {
          teams: state.teams,
          sort_teams: state.teams.cards.sort(function(team1, team2) {
            if (team1.name < team2.name) {
              return -1;
            }
            if (team1.name > team2.name) {
              return 1;
            }
          })
        }
    };

    const mapDispatchToProps = dispatch => {
        return {
            fetchTeams: params => dispatch(fetchTeams(params)),
        }
    };

export default connect(mapStateToProps, mapDispatchToProps)(SortButton)
